css:
.wordspo {
    display:flex;
    justify-content:space-between;
    flex-direction:row-reverse;
}
.wordspo,
.wordspo table,
.wordspo tr,
.wordspo td {
    page-break-inside: avoid !important;
    break-inside: avoid !important;
}
.print-format {
    padding:0px !important;
}

.contact-stamp {
    display: flex;
    justify-content: space-between;
}

.full-content {
    margin-left: 30px;
    margin-right: 30px;
}

/*.print-format, body {*/
/*    margin: 0 !important;*/
/*    padding: 0 !important;*/
/*}*/

@media print {
    @page {
        margin: 20px !important;
    }

    thead {
        display: table-row-group !important; /* prevents repeating header */
    }

    img {
        max-width: 100% !important;
        height: auto !important;
        object-fit: contain !important;
    }

    .item-image {
        max-height: none !important;
        height: auto !important;
        object-fit: contain !important;
    }

    table.items-table td {
        vertical-align: top !important;
    }

    body {
        margin: 0 !important;
        padding: 0 !important;
        font-size: 12px;
    }

    .print-format, .page-break {
        margin: 0 !important;
        padding: 0 !important;
        box-shadow: none !important;
    }

    .print-heading {
        display: none !important;
    }
}
